function addToCart(productName, price) {
      var cart = JSON.parse(localStorage.getItem('cart')) || [];
      cart.push({ name: productName, price: price });
      localStorage.setItem('cart', JSON.stringify(cart));
      alert('Produto adicionado ao carrinho!');
    }
function displayCart() {
    var cart = JSON.parse(localStorage.getItem('cart')) || [];

    var cartItemsElement = document.getElementById('cartItems');
    cartItemsElement.innerHTML = '';

    cart.forEach(function (item, index) {
        var cartItemElement = document.createElement('div');
        cartItemElement.classList.add('cart-item');
        cartItemElement.innerHTML = `<p>${item.name} - R$ ${item.price.toFixed(2)} <span class="remove-button" onclick="removeFromCart(${index})">Remover</span> <span class="add-button" onclick="addToCartAgain(${index})">Adicionar mais</span></p>`;
        cartItemsElement.appendChild(cartItemElement);
    });
}

function removeFromCart(index) {
    var cart = JSON.parse(localStorage.getItem('cart')) || [];
    cart.splice(index, 1);
    localStorage.setItem('cart', JSON.stringify(cart));
    displayCart();
}

function addToCartAgain(index) {
    var cart = JSON.parse(localStorage.getItem('cart')) || [];
    var item = cart[index];
    cart.push(item);
    localStorage.setItem('cart', JSON.stringify(cart));
    displayCart();
}

