        function adicionarAoCarrinho(productName, price) {
            var cart = JSON.parse(localStorage.getItem('cart')) || [];
            cart.push({ name: productName, price: price });
            localStorage.setItem('cart', JSON.stringify(cart));
            alert('Produto adicionado ao carrinho!');
        }

        var ordenacaoNomeCrescente = true;
        function ordenarPorNome() {
            ordenacaoNomeCrescente = !ordenacaoNomeCrescente;
            produtos.sort(function (a, b) {
                var nomeA = a.name.toUpperCase();
                var nomeB = b.name.toUpperCase();
                return ordenacaoNomeCrescente ? nomeA.localeCompare(nomeB) : nomeB.localeCompare(nomeA);
            });
            atualizarListaProdutos();
        }

        var ordenacaoPrecoCrescente = true;
        function ordenarPorPreco() {
            ordenacaoPrecoCrescente = !ordenacaoPrecoCrescente;
            produtos.sort(function (a, b) {
                return ordenacaoPrecoCrescente ? a.price - b.price : b.price - a.price;
            });
            atualizarListaProdutos();
        }

        function atualizarListaProdutos() {
            productsElement.innerHTML = '';
            produtos.forEach(function (produto) {
                var li = document.createElement('li');
                li.innerHTML = `
                    <div class="product">
                        <img src="${pizzaImages[produto.name]}" alt="${produto.name}">
                        <h3>${produto.name}</h3>
                        <p>R$ ${produto.price.toFixed(2)}</p>
                        <button class="buy-btn" onclick="adicionarAoCarrinho('${produto.name}', ${produto.price})">Adicionar ao Carrinho</button>
                    </div>
                `;
                productsElement.appendChild(li);
            });
        }
